#nullable enable

using System.Collections.Generic;
using GovUk.Education.ExploreEducationStatistics.Data.Services.ViewModels;
using Newtonsoft.Json.Linq;

namespace GovUk.Education.ExploreEducationStatistics.Data.Api.ViewModels;

/// <summary>
/// Permalink table which is generated by the frontend Next.js API and persisted in blob storage.
/// </summary>
public record PermalinkTableViewModel
{
    public string Caption { get; init; } = string.Empty;

    public List<FootnoteViewModel> Footnotes { get; init; } = new();

    /// <summary>
    /// Json representation of the table markup.
    /// </summary>
    public JObject Json { get; init; } = new();
}
